import { Component } from '@angular/core';

@Component({
  selector: 'app-full-stack',
  templateUrl: './full-stack.component.html',
  styleUrls: ['./full-stack.component.css']
})
export class FullStackComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Full-Stack Development</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background-image: url('https://example.com/your-background-image.jpg'); /* Specify the URL of your background image */
            background-size: cover;
            background-position: center;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
        }

        .fullstack-container {
            text-align: center;
            background-color: rgba(255, 255, 255, 0.8);
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            border-radius: 8px;
            padding: 20px;
            max-width: 400px;
            transition: transform 0.2s, box-shadow 0.2s;
        }

        .fullstack-container:hover {
            transform: scale(1.05);
            box-shadow: 0 8px 12px rgba(0, 0, 0, 0.2);
        }

        .fullstack-image {
            width: 100%;
        }

        h1 {
            color: #007BFF;
        }

        p {
            color: #333;
        }
    </style>
</head>
<body>
    <div class="fullstack-container">
        <h1>Full-Stack Development</h1>
        <p>Full-stack development involves working on both the front-end and back-end of web applications. It's a comprehensive approach that covers all aspects of web development, from designing user interfaces to server-side programming.</p>
        <p>Full-stack developers are well-versed in a variety of technologies and are capable of handling the entire development process, making them highly sought after in the tech industry.</p>
        <p>Whether it's building a simple website or a complex web application, full-stack developers have the skills to bring it to life.</p>
    </div>
</body>
</html>
