import { Component } from '@angular/core';
import { FormGroup,  FormBuilder,  Validators } from '@angular/forms';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent {
  title = 'Angular Form Validation';
  angForm!: FormGroup;
  constructor(private fb: FormBuilder) {
   this.createForm();
 }
  createForm() {
   this.angForm = this.fb.group({
      name: ['', Validators.required ],
      address: ['', Validators.required ]
   });
 }
}
<div class="container">
  <h1 style="text-align: center;">
    Welcome to {{title}}!!
  </h1>
  <br>
  <form [formGroup]="angForm" novalidate>
        <div class="form-group">

            <input class="form-control" formControlName="name" type="text" placeholder="Enter Name">
        </div>
        <div *ngIf="angForm.controls['name'].invalid && (angForm.controls['name'].dirty || angForm.controls['name'].touched)"
         class="alert alert-danger">
            <div *ngIf="angForm.controls['name'].errors">
              Name is required.
            </div>

 </div>
 <br><br>
         <div class="form-group">

            <input class="form-control" formControlName="address" type="text" placeholder="Enter Address">
        </div>
        <div *ngIf="angForm.controls['address'].invalid && (angForm.controls['address'].dirty || angForm.controls['address'].touched)"
           class="alert alert-danger">
            <div *ngIf="angForm.controls['email'].errors">
              addressl is required.
            </div>
        </div>
        <br><br>
        <button type="submit"
            [disabled]="angForm.pristine || angForm.invalid" class="btn btn-success">
            Save
        </button>
  </form>
  <br />
  <p>Form value: {{ angForm.value | json }}</p>
  <p>Form status: {{ angForm.status | json }}</p>
</div>
