import { Component } from '@angular/core';

@Component({
  selector: 'app-java',
  templateUrl: './java.component.html',
  styleUrls: ['./java.component.css']
})
export class JavaComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Java</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background-image: url('https://www.finoit.com/wp-content/uploads/2022/11/future-of-java-programming-language.jpg');
            background-size: cover;
            background-position: center;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
        }

        .java-container {
            text-align: center;
            background-color: rgba(255, 255, 255, 0.8);
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            border-radius: 8px;
            padding: 20px;
            max-width: 400px;
            transition: transform 0.2s, box-shadow 0.2s;
        }

        .java-container:hover {
            transform: scale(1.05);
            box-shadow: 0 8px 12px rgba(0, 0, 0, 0.2);
        }

        .java-image {
            width: 100%;
        }

        h1 {
            color: maroon;
        }

        p {
            color: #333;
        }
    </style>
</head>
<body>
    <div class="java-container">
        <h1>Java</h1>
        <p>
            Java is a popular, high-level programming language known for its platform independence. It is widely used for developing a variety of applications, including web applications, mobile apps, and backend systems.
        </p>
        <p>
            Java's key features include its "Write Once, Run Anywhere" capability, strong support for object-oriented programming, and a rich ecosystem of libraries and frameworks.
        </p>
        <p>
            Java is commonly used for building Android apps, enterprise applications, and web services. It has a large and active developer community, making it a versatile and reliable choice for software development.
        </p>
    </div>
</body>
</html>
