import { Component } from '@angular/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

}
<!DOCTYPE html>
<html>
<head>
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <style>
    body {
      font-family: Arial, Helvetica, sans-serif;
      background-color: #f2f2f2; /* Background color for the page */
      margin: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      height: 100vh;
    }

    .form-container {
      text-align: center;
      background-color: white;
      color: #333;
      border: 1px solid #ccc;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
      border-radius: 8px;
      padding: 20px;
      max-width: 600px; /* Adjust the width as needed */
      width: ;
    }

    .form-group {
      margin: 0 auto;
      padding: 10px;
    }

    .form-control {
      width: 200%;
      padding: 12px;
      border: 1px solid #ccc;
      margin-top: 6px;
      margin-bottom: 16px;
      resize: vertical;
    }

    .btn-primary {
      background-color: #04AA6D;
      color: white;
      padding: 12px 20px;
      border: none;
      cursor: pointer;
    }

    .btn-primary:hover {
      background-color: #45a049;
    }
  </style>
</head>
<body>

<div class="form-container">
  <h1>Register</h1>
  <form class="form-group">
    <label for="FullName">Full Name:</label>
    <input type="text" class="form-control" id="FullName" aria-describedby="nameHelp" placeholder="Enter Full Name">

    <label for="exampleInputEmail1">Email address:</label>
    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email">

    <label for="exampleInputPhone">Phone Number:</label>
    <input type="tel" class="form-control" id="exampleInputPhone" aria-describedby="phoneHelp" placeholder="Enter phone number">

    <label for="exampleInputPassword1">Password:</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password">

    <label for="retypePassword1">Retype Password:</label>
    <input type="password" class="form-control" id="retypePassword1" placeholder="Retype Password">

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>

</body>
</html>
